import { Button, FormControl, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, Spinner, useToast } from '@chakra-ui/react';
import React, { useState } from 'react'
import { useDisclosure } from '@chakra-ui/hooks';
import { ChatState } from '../../Context/chatProvider';
import axios from 'axios';
import UserListItem from '../UserAvatar/UserListItem';
import UserBadgeItem from '../UserAvatar/UserBadgeItem';
export default function GroupChatModal({children}) {

    const { isOpen, onOpen, onClose } = useDisclosure();
    const [groupChatName, setGroupChatName] = useState();
    const [selectedUsers, setSelectedUsers] = useState([]);
    const [search, setSearch] = useState("");
    const [searchResult, setSearchResult] = useState([]);
    const [loading, setLoading] = useState(false);
    const toast = useToast();  

    const {user, chats, setChats} = ChatState();

    const handleSearch = async(query)=>{
        setSearch(query)

        if(!query){
            return;
        }
        try {
            setLoading(true)
            const config = {
                headers:{
                  Authorization:`Bearer ${user.token}`
                },
              };

              const {data} = await axios.get(`/api/user?search=${search}`, config)
              setLoading(false)
              setSearchResult(data)
            } catch (error) {
                toast({
                    title: "Error",
                    status: "Failed to load users",
                    duration: 5000,
                    isClosable: true,
                    position: "top-left"
                  })
            
        }
    }    

    const handleSubmit = async()=>{

    }

    const handleGroup = (user)=>{
        if(selectedUsers.includes(user)){
            toast({
                title: "Error",
                status: "User Already added",
                duration: 5000,
                isClosable: true,
                position: "top-left"
              })
            return;
        }
        setSelectedUsers([...selectedUsers, user]);
        
    }

    const handleDelete = (user)=>{
        
    }
    return (
    <>
      <span onClick={onOpen}>{children}</span>

      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader
          fontSize="35px"
          fontFamily="Work sans"
          display="flex"
          justifyContent="center"
          >Create a new group</ModalHeader>
          <ModalCloseButton />
          <ModalBody display="flex" flexDir="column" alignItems="center">
              <FormControl>
                  <Input mb={3} placeholder="Chat Name" onChange={(e)=> setGroupChatName(e.target.value)}/>
              </FormControl>
              <FormControl>
                  <Input mb={1} placeholder="Add Users eg: Firo, Radi" onChange={(e)=> handleSearch(e.target.value)}/>
              </FormControl>
              {selectedUsers.map((u)=>(
                  <UserBadgeItem key={user._id} user={u} handleFunction={()=> handleDelete(u)}/>
              ))}
              {loading ? <Spinner ml="auto" display="flex"/> : (
                  searchResult?.slice(0,4).map(user=>(
                      <UserListItem key={user._id} user={user} handleFunction={()=>handleGroup(user)}/>
                  ))
              )}
          </ModalBody>

          <ModalFooter>
            <Button colorScheme='blue'  onClick={handleSubmit}>
              Creat Chat
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  )
}
